import { useState } from 'react';
import Button from './components/Button';
import FormAddFriend from './components/FormAddFriend';
import FormSplitBill from './components/FormSplitBill';
import FriendsList from './components/FriendsList';

const initialFriends = [
  {
    id: 118836,
    name: 'Clark',
    image: 'https://i.pravatar.cc/48?u=118836',
    balance: -7,
  },
  {
    id: 933372,
    name: 'Sarah',
    image: 'https://i.pravatar.cc/48?u=933372',
    balance: 20,
  },
  {
    id: 499476,
    name: 'Anthony',
    image: 'https://i.pravatar.cc/48?u=499476',
    balance: 0,
  },
];

const App = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [friends, setFriends] = useState(initialFriends);
  const [selectedFriend, setSelectedFriend] = useState(null);
  const splitBillOpenHandler = (friend) => {
    // setSelectedFriend(friend);
    setSelectedFriend((curr) => (curr?.id === friend.id ? null : friend));
    setIsOpen(false);
  };

  const showAddFormHandler = () => {
    setIsOpen((show) => !show);
  };
  const addNewFriend = (friend) => {
    setFriends((friends) => [...friends, friend]);
    setIsOpen(false);
  };
  const handleSplitBill = (value) => {
    setFriends();
  };
  return (
    <div className='app'>
      <div className='sidebar'>
        <FriendsList
          selectedFriend={selectedFriend}
          splitBillOpenHandler={splitBillOpenHandler}
          friends={friends}
        />
        {isOpen && <FormAddFriend addNewFriend={addNewFriend} />}
        <Button onClickEv={showAddFormHandler}>
          {isOpen ? 'Close' : 'Add Friend'}
        </Button>
      </div>
      {selectedFriend && (
        <FormSplitBill friend={selectedFriend} onSplitBill={handleSplitBill} />
      )}
    </div>
  );
};

export default App;
