import React, { useState } from 'react';
import Button from './Button';
const FormSplitBill = ({ friend }) => {
  const [billValue, setBillValue] = useState('');
  const [myExpense, setMyExpense] = useState('');
  const friendExpense = billValue - myExpense;
  return (
    <form className='form-split-bill'>
      <h2>Split a bill with {friend.name}</h2>
      <label>üí∞ Bill Value</label>
      <input value={billValue} type='number' />
      <label>üßç Your expenses</label>
      <input value={myExpense} type='number' />
      <label>üë¨ {friend.name} expenses</label>
      <input value={friendExpense} type='number' disabled />
      <label>ü§ë Who is paying the bill ?</label>
      <select>
        <option value='user'>You</option>
        <option value='friend'>{friend.name}</option>
      </select>
      <Button>Split Bill</Button>
    </form>
  );
};

export default FormSplitBill;
